import * as React from 'react';
import {useState} from 'react';

import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Button from '@mui/material/Button';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Link from '@mui/material/Link';
import {

    useTheme,
    useMediaQuery,
  } from "@material-ui/core";
function Navbar(props) {

    // const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down("md"));

    const [isDrawerOpen, setIsDrawerOpen] = useState(false);


    return (
        <Box sx={{ flexGrow: 1 }}>
        <ThemeProvider theme={darkTheme}>
        <AppBar position="fixed">
          <Toolbar>
            {isMobile?
            <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
            onClick = {()=>setIsDrawerOpen(true)}
          >
            <MenuIcon />
          </IconButton>:<></>
            }
{/* 
            <Typography
                variant="h5"
                component="p"
                color="textSecondary"
                className={classes.title}
              >
                Murali
              </Typography> */}


            <Typography variant="h6" component="p" 
            // sx={{ flexGrow: 1 }}
            >
              Portfolio
            </Typography>

            {isMobile?
              <Drawer open={isDrawerOpen} onClose={() => setIsDrawerOpen(false)}>
              <List>


                <ListItem button>
                <Link href="#about" color="inherit">About</Link>
                </ListItem>

                <ListItem button>
                <Link href="#skills" color="inherit">Skills</Link>
                </ListItem>

                <ListItem button>
                  <Link href="#experiences" color="inherit">Experiences</Link>
                </ListItem>

                <ListItem button>
                <Link href="#contact" color="inherit">Contact me</Link>
                </ListItem>
              </List>
            </Drawer>
            :
            <div>

            <Button>
              <Link href="#about" color="inherit">
                About
              </Link>
            </Button>

            <Button>
              <Link href="#skills" color="inherit">
                Skills
              </Link>
            </Button>

            <Button >
              <Link href="#experiences" color="inherit">
                Experiences
              </Link>
            </Button>


            <Button>
              <Link href="#contact" color="inherit">
                Contact Me
              </Link>
            </Button>


            </div>
            }



            {/* <Button color="inherit">Login</Button> */}
          </Toolbar>
        </AppBar>
        </ThemeProvider>
      </Box>
    );
}

export default Navbar;
